import "core-js/modules/es.array.push.js";
import { mapActions } from "vuex";
export default {
  data: () => ({
    valid: true,
    item: {
      itemname: "",
      itemcategory: "",
      itemdes: "",
      status: "",
      itemprice: ""
    },
    nameRules: [v => !!v || "Name is required", v => v && v.length <= 10 || "Name must be less than 10 characters"],
    categoryRules: [v => !!v || "Category is required", v => v && v.length <= 10 || "Name must be less than 10 characters"],
    DescriptionRules: [v => !!v || "Description is required", v => v && v.length <= 50 || "Description must be valid"],
    PriceRules: [v => !!v || "Price is required"]
  }),
  filters: {
    currency: function (value) {
      return "$ " + parseFloat(value).toFixed(2);
    }
  },
  methods: {
    ...mapActions(["setItem"]),
    onSubmit() {
      this.$refs.form.validate();
      this.setItem({
        itemname: this.item.itemname,
        itemcategory: this.item.itemcategory,
        itemdes: this.item.itemdes,
        status: this.item.status,
        itemprice: this.item.itemprice
      });
      this.item.itemname = "", this.item.itemcategory = "", this.item.itemdes = "", this.item.status = "", this.item.itemprice = "";
      this.$refs.form.reset();
      this.$swal("Item Added Successfully");
      this.$router.push("/allitem");
    }
  }
};
